const request = require('request');

const options = {
  method: 'POST',
  url: 'https://platform.strings.dev/api/translate',
  headers: {'content-type': 'application/json', 'x-api-key': 'REPLACE_KEY_VALUE'},
  body: {
    context: 'Do you have any safety concerns?',
    text: 'No hay suficientes cascos para la tripulación.',
    targetLanguage: 'en'
  },
  json: true
};

request(options, function (error, response, body) {
  if (error) throw new Error(error);

  console.log(body);
});

const http = require("https");

const options = {
  "method": "POST",
  "hostname": "platform.strings.dev",
  "port": null,
  "path": "/api/translate",
  "headers": {
    "content-type": "application/json",
    "x-api-key": "REPLACE_KEY_VALUE"
  }
};

const req = http.request(options, function (res) {
  const chunks = [];

  res.on("data", function (chunk) {
    chunks.push(chunk);
  });

  res.on("end", function () {
    const body = Buffer.concat(chunks);
    console.log(body.toString());
  });
});

req.write(JSON.stringify({
  context: 'Do you have any safety concerns?',
  text: 'No hay suficientes cascos para la tripulación.',
  targetLanguage: 'en'
}));
req.end();

var axios = require("axios").default;

var options = {
  method: 'POST',
  url: 'https://platform.strings.dev/api/translate',
  headers: {'content-type': 'application/json', 'x-api-key': 'REPLACE_KEY_VALUE'},
  data: {
    context: 'Do you have any safety concerns?',
    text: 'No hay suficientes cascos para la tripulación.',
    targetLanguage: 'en'
  }
};

axios.request(options).then(function (response) {
  console.log(response.data);
}).catch(function (error) {
  console.error(error);
});

const fetch = require('node-fetch');

let url = 'https://platform.strings.dev/api/translate';

let options = {
  method: 'POST',
  headers: {'content-type': 'application/json', 'x-api-key': 'REPLACE_KEY_VALUE'},
  body: '{"context":"Do you have any safety concerns?","text":"No hay suficientes cascos para la tripulación.","targetLanguage":"en"}'
};

fetch(url, options)
  .then(res => res.json())
  .then(json => console.log(json))
  .catch(err => console.error('error:' + err));
